func finalFloor(i string) int {
	u := strings.Count(i, "(")
	d := strings.Count(i, ")")
	return u - d
}

func firstBasementPos(i string) (int, error) {
	floor := 0
	for i, c := range input {
		if string(c) == "(" {
			floor++
		} else if string(c) == ")" {
			floor--
		}

		if floor == -1 {
			return i + 1, nil
		}
	}
	return 0, errors.New("Basement never reached!")
}

func main() {
	fmt.Println("Part One:", finalFloor(input))

	pos, err := firstBasementPos(input)
	if err != nil {
		fmt.Println(err)
	} else {
		fmt.Println("Part Two:", pos)
	}
}
