package main

import "fmt"

// Returns the final floor, and the position of the first time you reach the basement
func dayOne(inpt *string) (int, int) {
	var (
		floor int
		pos   int = -1
	)
	for i, c := range *inpt {
		if string(c) == "(" {
			floor++
		} else if string(c) == ")" {
			floor--
		}

		if floor == -1 && pos == -1 {
			pos = i + 1
		}
	}
	return floor, pos
}

