func finalFloor(i string) int {
	u := strings.Count(i, "(")
	d := strings.Count(i, ")")
	return u - d
}

func firstBasementPos(i string) int {
	pos, floor := 0, 0
	for i, c := range input {
		if string(c) == "(" {
			floor++
		} else if string(c) == ")" {
			floor--
		}

		if floor == -1 && pos == 0 {
			pos = i + 1
		}
	}
	return pos
}

func main() {
	fmt.Println("Part One:", finalFloor(input))
	fmt.Println("Part Two:", firstBasementPos(input))
}
